import { Connection, PublicKey } from '@solana/web3.js';

declare class SolName {
    connection: Connection;
    getDomainName({ address }: {
        address: string;
    }): Promise<string>;
    getAddress({ name }: {
        name: string;
    }): Promise<PublicKey>;
}

/**
 * Creates a new instance of non-EVM based chains
 */
declare function createSolName(): SolName;

export { createSolName };
